    <script>
    $(document).ready(function() {
        
        function toggleCarouselItems() {
            var $carouselInner = $('#carouselExample .carousel-inner');
            var $carouselItems = $carouselInner.find('.carousel-item');

            // Hide all carousel items and show only the first one
            $carouselItems.hide();
            $carouselItems.eq(0).show();

            // Handle the slide event to show the next item
            $('#carouselExample').on('slide.bs.carousel', function () {
                var $activeItem = $carouselInner.find('.carousel-item.active');
                var $nextItem = $activeItem.next('.carousel-item');
                
                if ($nextItem.length === 0) {
                    $nextItem = $carouselItems.eq(0);
                }

                $activeItem.hide();
                $nextItem.show();
            });
        }

        toggleCarouselItems();
    });
</script>

